
public class Arithmetic {

	//This is a program to explain Exceptions
	//----------------------------
	//----------------------------
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		try{
		int x=50/0;
		}
		catch (ArithmeticException x)
		{
		System.out.println(x);	
		}
		int a=10; int b=20;
		System.out.println(a+b);

	}

}
